import React from 'react'
import { StyleSheet, TextInput, View } from 'react-native'

const Input = () => {
  return (
    <View>
        <TextInput  style={styles.input} placeholder="masukan nilai"/>
    </View>
  )
}

const styles = StyleSheet.create({
    input: {
        width: '100%',
        height: 40,
        borderColor: '#448f92',
        borderStyle: 'solid',
        paddingHorizontal : 10,
        outlineStyle: "none",
        backgroundColor: 'white',
        borderRadius: 10,
        fontSize: 20
}})

export default Input
